
CREATE DATABASE `evoting`;

USE `evoting`;

grant all privileges on evoting.* to 'webapp'@'%';
flush privileges;

DROP TABLE IF EXISTS `candidates`;
create table `candidates` (
	`CandidateID` INT PRIMARY KEY,
	`FirstName` VARCHAR(50),
	`LastName` VARCHAR(50),
	`PoliticalParty` VARCHAR(11),
	`RunningMate` VARCHAR(50),
	`Platform` TEXT
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `votes`;
create table `votes` (
	`VoterID` INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
	`VoterName` VARCHAR(50),
	`SSN` VARCHAR(50),
	`Email` VARCHAR(50),
	`CandidateVote` VARCHAR(50)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `complaints`;
create table `complaints` (
    `ComplaintID` INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
	`AuditorName` VARCHAR(50),
	`Candidate` VARCHAR(50),
	`VoterName` VARCHAR(50),
	`Complaint` TEXT
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `bans`;
create table `bans` (
	`BanID` INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
	`AdminName` VARCHAR(50),
	`BannedSSN` VARCHAR(50),
	`Reason` VARCHAR(23),
	`Length` VARCHAR(9),
	`Details` TEXT
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

